var ipc = require('electron').ipcMain;
module.exports=(channel,webContents,arg)=>{
    return new Promise((resolve,reject)=>{
        ipc.once(`${channel}-reply`, function(event, result) {
            resolve(result);
        });
        webContents.send(channel,arg);
    });
};